import { useQuery } from '@tanstack/react-query';
import axios from 'axios';

type State = 'data' | 'pageInfo';

type PortfolioCommentAPI = {
  portfolioCommentId: number;
  userId: number;
  portfolioId: number;
  userName: string;
  content: string;
  createdAt: string;
  updatedAt: string;
};

export const useDetailPageComment = (state?: State) => {
  // 추후에 API 파일로 이동하기
  const getCommentData = async (): Promise<PortfolioCommentAPI> => {
    const commentData = await axios.get(`http://localhost:3003/${state}`);
    return commentData.data;
  };

  const { isLoading, isError, data, error } = useQuery({
    queryKey: ['comment'],
    queryFn: () => getCommentData(),
  });
  console.log(isLoading, isError, data, error);
  return { isLoading, isError, data, error };
};
