import * as S from './Detail.style';

// react component
import DetailTitle from '../components/Detail/DetailTitle';
import ProjectImg from '../components/Detail/ProjectImg';
import Description from '../components/Detail/Description';
import ProjectContent from '../components/Detail/ProjectContent';
import Comment from '../components/Detail/Comment';
import LikeBtn from '../components/Detail/LikeBtn';

// custom Hooks
import { useGetPortfolio } from '../hooks/useGetPortfolio';

// react-router-dom
import { useParams } from 'react-router-dom';

// axios
import axios from 'axios';

// react hook
import { useEffect, useState } from 'react';

type portfolios = {
  portfolioId: number;
  userId: number;
  name: string;
  title: string;
  gitLink: string;
  distributionLink: string;
  description: string;
  content: string;
  views: number;
  createdAt: number[];
  updatedAt: number[];
};

function Detail() {
  // 타입 추가를 해야 한다?

  const { portfolioId } = useParams();

  const { getPortfolioLoading, getPortfolioError, PortfolioInfo } = useGetPortfolio(
    Number(portfolioId),
  );

  const [dummyData, setDummyData] = useState<portfolios | never>();
  // axios처리를 진행해야 겠다

  const { name, content, description, distributionLink, gitLink, title, views } = PortfolioInfo!;

  return (
    <S.Container>
      <LikeBtn />
      <DetailTitle
        title={title}
        name={name}
        gitLink={gitLink}
        distributionLink={distributionLink}
      />
      <ProjectImg />
      <Description />
      <ProjectContent />
      <Comment />
    </S.Container>
  );
}

export default Detail;
