import * as S from './Comment.style';
// react hooks
import React, { useState, useEffect } from 'react';

// react component
import CommentItem from './CommentItem';

// AJAX library
import axios from 'axios';

// 상세 페이지 포트폴리오 댓글 컴포넌트
function Comment() {
  const [comment, setComment] = useState<string>('');
  const [dummyData, setDummyData] = useState([]);
  useEffect(() => {
    axios.get('http://localhost:3003/data').then((res) => {
      console.log(res.data);
      setDummyData(res.data);
    });
  }, []);

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setComment('');
  };

  const handleCommentChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setComment(event.target.value);
  };
  return (
    <S.Container>
      <S.CommentWrapper>
        <S.CommentShow>
          {dummyData.map((data) => {
            const { userId, content, createAt, userName } = data;
            return (
              <CommentItem key={userId} content={content} createAt={createAt} userName={userName} />
            );
          })}
        </S.CommentShow>
        <S.CommentForm onSubmit={handleSubmit}>
          <S.CommentArea
            placeholder='Enter your comment here'
            value={comment}
            onChange={handleCommentChange}
          />
          <S.YellowBtnCustom>COMMENT</S.YellowBtnCustom>
        </S.CommentForm>
      </S.CommentWrapper>
    </S.Container>
  );
}

export default Comment;
