import React, { useState } from 'react';

import { useAppDispatch, useAppSelector } from '../../hooks/reduxHook';
import { changeTheme } from '../../store/slice/themeSlice';

import styled from 'styled-components';

interface ToggleProps {
  themeHandler: () => void;
}

const Toggle: React.FC<ToggleProps> = ({ themeHandler }) => {
  const currentTheme = useAppSelector((state) => state.theme);
  const dispatch = useAppDispatch();

  const [isChecked, setIsChecked] = useState(false);

  const handleToggle = () => {
    console.log('here');
    const newCheckedState = !isChecked;
    setIsChecked(newCheckedState);
  };

  return (
    <StyledToggle onClick={handleToggle} isChecked={isChecked}>
      <ToggleSwitch isChecked={isChecked} />
    </StyledToggle>
  );
};
//  background-color: ${(props) => (props.isChecked ? props.theme.themeStyle.cardColor : '#ccc')};

const StyledToggle = styled.div<{ isChecked: boolean }>`
  display: inline-block;
  position: relative;
  width: 60px;
  height: 34px;
  border-radius: 34px;
  cursor: pointer;
  transition: background-color 0.3s;
`;

const ToggleSwitch = styled.div<{ isChecked: boolean }>`
  position: absolute;
  top: 2px;
  left: ${(props) => (props.isChecked ? '30px' : '2px')};
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #fff;
  box-shadow: 0 0 2px rgba(0, 0, 0, 0.3);
  transition: left 0.3s;
`;

export default Toggle;
