// react-query
import { useMutation, useQueryClient } from '@tanstack/react-query';

// axios
import axios from 'axios';

// typess
import { PostPortfolioComment } from '../types/index';

type usePostPortfolioCommentProps = {
  portfolioId?: string;
  postCommentData: PostPortfolioComment;
};

export const usePostPortfolioComment = ({
  portfolioId,
  postCommentData,
}: usePostPortfolioCommentProps) => {
  // !: URL Path 추가 확인 필요함!
  const PostCommentData = async (portfolioId?: string) => {
    return await axios.post(
      `${process.env.REACT_APP_API_URL}/api/portfoliocomments/portfolios/${portfolioId}`,
      postCommentData,
    );
  };

  const queryClient = useQueryClient();

  const { mutate } = useMutation({
    // 위 타입을 지정해줘야 함!
    mutationFn: () => PostCommentData(),
    onSuccess: () => {
      // * setData로 특정 id에 해당 하는 데이터 사전 처리도 가능할듯!
      queryClient.invalidateQueries(['comment']);
    },
    onError: (error) => {
      // *: error 헨들링 하기
      console.log(error);
    },
  });
  return { mutate };
};
