import jwtDecode from 'jwt-decode';

// redux
import { useAppSelector } from '../hooks/reduxHook';

// !: types로 이동해야 함!
type MyToken = {
  userId: number;
  exp: number;
  iat: number;
};

export const getUserIdFromAccessToken = (token: string | undefined) => {
  if (token !== null) {
    return jwtDecode<MyToken>(token).userId;
  }
};
