import styled from 'styled-components';

// constants
import { COLOR } from '../../constants';

const { mainColor } = COLOR;

export const StyledLoading = styled.div`
  display: flex;
  position: relative;
  width: 32px;
  height: 32px;
  div {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background-color: ${mainColor};
    opacity: 0.6;
    animation: loading 1.4s cubic-bezier(0, 0.2, 0.8, 1) infinite;
  }

  @keyframes loading {
    0% {
      transform: scale(0);
      opacity: 0.7;
    }
    50% {
      opacity: 0.1;
    }
    100% {
      transform: scale(1);
      opacity: 0.7;
    }
  }
`;
