// react-query
import { useMutation, useQueryClient } from '@tanstack/react-query';

// axios
import axios from 'axios';

// typess
import { PostPortfolioComment } from '../types/index';

// util
import { getUserIdFromAccessToken } from '../utils/getUserIdFromAccessToken';

type PostTemplate = {
  userId: number | undefined;
  portfolioId: string;
  comments: string;
};

export const usePostPortfolioComment = (portfolioId: string, comments: string) => {
  // string | undefined을 가진다.
  const userId = getUserIdFromAccessToken();

  // !: URL Path 추가 확인 필요함!
  const PostCommentData = async ({ userId, portfolioId, comments }: PostTemplate) => {
    return await axios.post(`${process.env.REACT_APP_API_URL}/api/portfoliocomments`, {
      userId,
      portfolioId,
      content: comments,
    });
  };

  const queryClient = useQueryClient();

  const { mutate: postComment } = useMutation({
    // 위 타입을 지정해줘야 함!
    mutationFn: PostCommentData,
    onSuccess: () => {
      // * setData로 특정 id에 해당 하는 데이터 사전 처리도 가능할듯!
      queryClient.invalidateQueries(['comment']);
    },
    onError: (error) => {
      // *: error 헨들링 하기
      console.log(error);
    },
  });
  // 이래서 ref로 처리를 하는 것인가?(고민해본 결과 그냥 useState를 사용하는 것이 좋을 수도)
  const postCommentAction = (comments: string) => {
    postComment({ userId, portfolioId, comments });
  };
  return { postCommentAction };
};
