import * as S from './Comment.style';
// react hooks
import React, { useState } from 'react';

// react component
import CommentItem from './CommentItem';
import Loading from '../common/Loading';

// custom hooks
import { useGetPortfolioComment } from '../../hooks/useGetPortfolioComment';
import { usePostPortfolioComment } from '../../hooks/usePostPortfolioComment';

// types
import { GetPortfolioCommentById } from '../../types/index';

// react-query
import { useQueryClient, useMutation } from '@tanstack/react-query';

// react-router-dom
import { useParams } from 'react-router-dom';

// axios
import axios from 'axios';

// 상세 페이지 포트폴리오 댓글 컴포넌트
function Comment() {
  const [comments, setComments] = useState<string>('');

  const { id } = useParams();

  // !: portfolioId를 넣어야 한다. id 값은 무조건 존재할 수 밖에 없음?
  const { PortfoliocommentLoading, PortfolioCommentData } = useGetPortfolioComment(id!);

  // !: 태민님의 답변에 따라 수정을 해야 함!
  const { postComment } = usePostPortfolioComment(id!);

  const postCommentData = {
    userId: 1,
    portfolioId: 1,
    content: comments,
  };

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    postComment({ portfolioId: '1', postComment: postCommentData });
    setComments('');
  };

  const handleCommentChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setComments(event.target.value);
  };

  // !: data가 없을 때 어떻게 표시되는지 꼭 확인해야 한다.
  return (
    <S.Container>
      {PortfoliocommentLoading ? (
        <Loading />
      ) : (
        <S.CommentWrapper>
          <S.CommentShow>
            {(PortfolioCommentData ?? []).map((comment: GetPortfolioCommentById) => {
              const { userId, content, createdAt, userName, portfolioCommentId, portfolioId } =
                comment;
              return (
                <CommentItem
                  key={id}
                  userId={userId}
                  content={content}
                  createdAt={createdAt}
                  userName={userName}
                  portfolioCommentId={portfolioCommentId}
                  portfolioId={portfolioId}
                />
              );
            })}
          </S.CommentShow>
          <S.CommentForm onSubmit={handleSubmit}>
            <S.CommentArea
              placeholder='Enter your comment here'
              value={comments}
              onChange={handleCommentChange}
            />
            <S.YellowBtnCustom>COMMENT</S.YellowBtnCustom>
          </S.CommentForm>
        </S.CommentWrapper>
      )}
    </S.Container>
  );
}

export default Comment;
