import styled from 'styled-components';

export const StyledToggle = styled.div<{ isChecked: boolean }>`
  display: inline-block;
  position: relative;
  width: 60px;
  height: 34px;

  border-radius: 34px;
  cursor: pointer;
  background-color: ${(props) => (props.isChecked ? props.theme.themeStyle.cardColor : '#ccc')};
  transition: background-color 0.3s;
`;

export const ToggleSwitch = styled.div<{ isChecked: boolean }>`
  position: absolute;
  top: 2px;
  //밑을 dark,ligth theme으로 변경하면 될듯?
  left: ${(props) => (props.isChecked ? '30px' : '2px')};
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #fff;
  box-shadow: 0 0 2px rgba(0, 0, 0, 0.3);
  transition: left 0.3s;
`;
