// react-query
import { useQuery, QueryKey } from '@tanstack/react-query';

// axios
import axios from 'axios';

// types
import { GetPortfolioCommentById } from '../types/index';

// api
import { PortfolioCommentAPI } from '../api/client';

const { getPortfolioComment } = PortfolioCommentAPI;

export const useGetPortfolioComment = (portfolioId: string) => {
  // !: API 파일로 이동해야 함!
  const getCommentData = async (portfolioId: string): Promise<GetPortfolioCommentById[]> => {
    const commentData = await axios.get(
      `${process.env.REACT_APP_API_URL}/api/portfoliocomments/portfolios/${portfolioId}`,
    );
    return commentData.data.data;
  };
  const { isLoading, isError, data, error } = useQuery({
    queryKey: ['comment'],
    queryFn: () => getPortfolioComment,
  });
  return { isLoading, isError, data, error };
};
