import jwtDecode from 'jwt-decode';

// !: types로 이동해야 함!
type MyToken = {
  userId: number;
  exp: number;
  iat: number;
};

export const getUserIdFromAccessToken = (token: string) => {
  return jwtDecode<MyToken>(token).userId;
};
