// react-query
import { useMutation, useQueryClient } from '@tanstack/react-query';

// axios
import axios from 'axios';

// typess
import { PostPortfolioComment } from '../types/index';

type usePostPortfolioCommentProps = {
  portfolioId: string;
  postComment: PostPortfolioComment;
};
type PostPortfolioParams = {
  portfolioId: string | undefined;
  comments: string;
};

export const usePostPortfolioComment = (portfolioId: string, comments: string) => {
  // !: URL Path 추가 확인 필요함!
  const PostCommentData = async ({ portfolioId, postComment }: usePostPortfolioCommentProps) => {
    return await axios.post(
      `${process.env.REACT_APP_API_URL}/api/portfoliocomments/portfolios/${portfolioId}`,
      postComment,
    );
  };

  const queryClient = useQueryClient();

  const { mutate: postComment } = useMutation({
    // 위 타입을 지정해줘야 함!
    mutationFn: PostCommentData,
    onSuccess: () => {
      // * setData로 특정 id에 해당 하는 데이터 사전 처리도 가능할듯!
      queryClient.invalidateQueries(['comment']);
    },
    onError: (error) => {
      // *: error 헨들링 하기
      console.log(error);
    },
  });
  // 이래서 ref로 처리를 하는 것인가?(고민해본 결과 그냥 useState를 사용하는 것이 좋을 수도)
  const postCommentAction = (comments: string) => {
    postComment({});
  };
  return { postComment };
};
