import styled from 'styled-components';
import { RxCross2, RxPencil2, RxCheck } from 'react-icons/rx';
import { COLOR } from '../../constants/index';
const { subFontColor } = COLOR;

export const Feedback = styled.li`
  padding: 10px;
  padding-bottom: 40px;
  margin: 10px 0;
  border-radius: 10px;
  box-shadow: 0px 2px 2px ${subFontColor};
  position: relative;
  background-color: ${({ theme }) => theme.themeStyle.cardColor};
  svg {
    font-size: 1.3rem;
    cursor: pointer;
  }
  textarea {
    height: 80px;
    width: 100%;
    border-radius: 10px;
    border: 0;
    outline: none;
    background-color: ${({ theme }) => theme.themeStyle.cardColor};
    color: ${(props) => props.theme.themeStyle.fontColor};
    resize: none;
  }
`;
export const ImgBox = styled.div`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  overflow: hidden;
  margin: 0 5px;
  img {
    width: 20px;
    height: 20px;
  }
`;
export const FeedbackUser = styled.div`
  display: flex;
  position: absolute;
  bottom: 10px;
  right: 10px;
  height: 20px;
  align-items: center;
  font-size: 0.8rem;
`;

export const DelBtn = styled(RxCross2)`
  margin-right: 1rem;
`;
export const EditBtn = styled(RxPencil2)``;
export const SubmitBtn = styled(RxCheck)`
  position: absolute;
  top: 10px;
  right: 10px;
`;
