import styled from 'styled-components';

// icons
import { RxHeartFilled } from 'react-icons/rx';

export const LikeBtnWrapper = styled.section`
  /* /* display: flex; */
  flex-direction: column;
  align-items: center; */
  width: 45px;
  height: 45px;
  position: fixed;
  top: 50%;
  right: 20px;
`;

export const LikeIcon = styled(RxHeartFilled)`
  font-size: 30px;
`;
