import React, { useState } from 'react';
import styled from 'styled-components';

interface ToggleProps {
  checked: boolean;
  themeHandler: () => void;
}

const Toggle: React.FC<ToggleProps> = ({ checked, themeHandler }) => {
  const [isChecked, setIsChecked] = useState(checked);

  // const handleToggle = () => {
  //   console.log('here');
  //   if (disabled) return;
  //   const newCheckedState = !isChecked;
  //   setIsChecked(newCheckedState);
  //   // onChange(newCheckedState);
  // };

  return (
    <StyledToggle
      // className={className}
      onClick={themeHandler}
    >
      <ToggleSwitch />
    </StyledToggle>
  );
};

const StyledToggle = styled.div<{ isChecked: boolean }>`
  display: inline-block;
  position: relative;
  width: 60px;
  height: 34px;
  background-color: ${(props) => (props.isChecked ? props.theme.themeStyle.cardColor : '#ccc')};
  border-radius: 34px;
  cursor: pointer;
  transition: background-color 0.3s;
`;

const ToggleSwitch = styled.div<{ isChecked: boolean }>`
  position: absolute;
  top: 2px;
  left: ${(props) => (props.isChecked ? '30px' : '2px')};
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: #fff;
  box-shadow: 0 0 2px rgba(0, 0, 0, 0.3);
  transition: left 0.3s;
`;

export default Toggle;
