import jwtDecode from 'jwt-decode';

// redux
import { useAppSelector } from '../hooks/reduxHook';

// !: types로 이동해야 함!
type MyToken = {
  userId: number;
  exp: number;
  iat: number;
};

const token = useAppSelector((state) => state.login.accessToken);

export const getUserIdFromAccessToken = () => {
  return jwtDecode<MyToken>(token).userId;
};
