import * as S from './Comment.style';
// react hooks
import React, { useState } from 'react';

// react component
import CommentItem from './CommentItem';
import Loading from '../common/Loading';

// custom hooks
import { useDetailPageComment } from '../../hooks/useDetailPageComment';

// types
import { PortfolioCommentAPI } from '../../types/index';

// react-query
import { useQueryClient, useMutation } from '@tanstack/react-query';

import axios from 'axios';

// 상세 페이지 포트폴리오 댓글 컴포넌트
function Comment() {
  const [comments, setComments] = useState<string>('');

  const { isLoading, data } = useDetailPageComment('data');

  // custom hook으로 따로 처리를 해야 한다.
  // 여기에서 userId는 어디에서 담아 오는 것인가? JWT를 담아서 보내면 되는 것인가?
  // 위와 같은 방법으로 유저를 식별하는 것인가?(아니면 디코드를 해서 값을 받아와야 하는 건가?)
  // 이것도 추가로 논의가 필요해 보인다.

  const newComment = {
    portfolioCommentId: 6,
    portfolioId: 6,
    userName: 'test',
    content: comments,
    createdAt: '2023-05-08T14:20:53.326328',
    updatedAt: '2023-05-08T14:20:53.326328',
  };

  const queryClient = useQueryClient();

  // comments의 id값을 받아온 것이다
  // post 방식은 벨러퍼트의 방식을 참고해야 할듯하다! 조금 더 생각해보자!

  // !: any 타입 변경이 필요하다! post이기 때문에 다른 방식으로 진행될듯!
  // !: 추가 검색이 필요하다.

  const newCommentMutation = useMutation({
    mutationFn: (newComment: any) => {
      return axios.post('http://localhost:3003/', {
        ...newComment,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries(['comment']);
    },
  });

  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    newCommentMutation.mutate(newComment);
    setComments('');
  };

  const handleCommentChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setComments(event.target.value);
  };

  // !: data가 없을 때 어떻게 표시되는지 꼭 확인해야 한다.
  return (
    <S.Container>
      {isLoading ? (
        <Loading />
      ) : (
        <S.CommentWrapper>
          <S.CommentShow>
            {data?.map((comment: PortfolioCommentAPI) => {
              const { id, content, createdAt, userName, portfolioCommentId, portfolioId } = comment;
              return (
                <CommentItem
                  key={id}
                  id={id}
                  content={content}
                  createdAt={createdAt}
                  userName={userName}
                  portfolioCommentId={portfolioCommentId}
                  portfolioId={portfolioId}
                />
              );
            })}
          </S.CommentShow>
          <S.CommentForm onSubmit={handleSubmit}>
            <S.CommentArea
              placeholder='Enter your comment here'
              value={comments}
              onChange={handleCommentChange}
            />
            <S.YellowBtnCustom>COMMENT</S.YellowBtnCustom>
          </S.CommentForm>
        </S.CommentWrapper>
      )}
    </S.Container>
  );
}

export default Comment;
