// react-query
import { useQuery } from '@tanstack/react-query';

// axios
import axios from 'axios';

// types
import { GetPortfolioCommentById } from '../types/index';

// API
import { PortfolioCommentAPI } from '../api/client';

export const useGetPortfolioComment = (portfolioId: string) => {
  // !: API 파일로 이동해야 함!

  const { isLoading, isError, data, error } = useQuery<GetPortfolioCommentById[], Error>({
    queryKey: ['comment'],
    queryFn: PortfolioCommentAPI.getPortfolioCommentById,
    onError: (error) => {
      // *: error 헨들링을 해줘야 한다.
      console.log('useQuery', error);
    },
  });
  return { isLoading, isError, data, error };
};
