// style-component
import * as S from './Header.style';

// react component
import Toggle from './Toggle';
import { YellowBtn } from '../../components/common/Button.style';
import Hamburger from './Hamburger';
import NavLink from './NavLink';

// custom hooks
import { useRouter } from '../../hooks/useRouter';

// react hooks
import { useState } from 'react';

// redux
import { useAppSelector } from '../../hooks/reduxHook';

function Header() {
  const { routeTo } = useRouter();

  const isLogin = useAppSelector((state) => state.login.isLogin);

  const handleLogoClick = () => {
    routeTo('/');
  };

  const handleLoginClick = () => {
    routeTo('/Login');
  };

  const [openNav, setOpenNav] = useState<boolean>(false);

  return (
    <S.Header>
      <S.Logo onClick={handleLogoClick}>Logo</S.Logo>
      {openNav && <NavLink setOpenNav={setOpenNav} />}
      <S.ButtonContainer>
        <Toggle />
        {/* {여기에 사용자 프로필 사진을 보여주면 된다, 아닐 경우 null을 보여준다} */}
        {isLogin ? <img /> : null}
        <YellowBtn onClick={handleLoginClick} className='header-login'>
          {isLogin ? 'Logout' : 'Login'}
        </YellowBtn>
        {isLogin ? null : <S.SignUp className='header-signup'>회원가입</S.SignUp>}
        <Hamburger openNav={openNav} setOpenNav={setOpenNav} />
      </S.ButtonContainer>
    </S.Header>
  );
}

export default Header;
